var $bm_rt;
var controlLayer = thisComp.layer('Expression Controls');
var speedControl = controlLayer.effect('Speed Control')('Slider');
var maxDurationControl = controlLayer.effect('Max Duration')('Slider');
var startOffsetControl = controlLayer.effect('Start Offset')('Slider');
seedRandom(index, true);
var randomSpeed = random(0.5, 1.5);
var maxDuration = maxDurationControl;
var speedFactor = linear(speedControl, 1, 100, 0.1, 2);
var startOffsetRandom = random(10, startOffsetControl);
var t = $bm_div($bm_mul(time, randomSpeed), speedFactor);
var startProgress = $bm_mul($bm_div($bm_mod($bm_sum(t, startOffsetRandom), maxDuration), maxDuration), 100);
startProgress = startProgress < 0 ? $bm_sum(startProgress, 100) : startProgress;
$bm_rt = startProgress;

